/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react'
import axios from 'axios'
import { BrowserRouter as Router,Link, Route, Switch } from 'react-router-dom'
import Reg from '../Register/Reg'
import './InputLogin.css'





export default class InputLogin extends Component {



      handleSubmit = e => {
          e.preventDefault();

        const con = {
            method: 'post',
            headers: {
              'Access-Control-Allow-Origin': '*',
            }}

        const data  = {
              email : this.email,
              password: this.password,
              usepermis_id : this.usepermis_id,
          };

        axios.post('http://localhost:9999/login',data,con )
        .then(res => {
            console.log(res);

        })
        .catch(err => {
            console.log(err)
        })
      };

    render() {
        console.log("========Login======")
        return (
            <Router>
            <div className="row">

                    <div className="col">

                    <form onSubmit={this.handleSubmit} >

                        <div className="form-group">
                            <label >Email</label>
                            <input
                            type="email"
                            name="email"
                            onChange={e => this.email = e.target.value} 
                            className="form-control" 
                            placeholder="Email"
                            required />

                            <label>Password</label>
                            <input 
                            type="password"
                            name="password"
                            onChange={e => this.password = e.target.value} 
                            className="form-control" 
                            placeholder="Password"
                            required />

                            <button type="submit" className="btn btn-primary btn-block">Submit</button>

                            <Link to={'/Register/Reg'} className="nav-link"> สมัครสมาชิก </Link>
                        <hr />
                        <Switch>
                            <Route path='/Register/Reg' component={Reg} />
                        </Switch>

                        </div>


                    </form>
                    </div>

            </div>
            </Router>

        )
    }
}
