import React, { Component } from 'react'
import Newdetail from './Newdetail/Newdetail'

import axios from 'axios'


import './News.css'

 const apinewtopic ="http://localhost:9999/new"

export default class News extends Component {

    constructor(props){
        super(props)

        this.handdleonClick = this.handdleonClick.bind(this)
        this.renderArticles = this.renderArticles.bind(this)


        this.state ={
            newtopic: [],
            articles:[],
            id : "",
            topic : "",
            detail : ""
        }
    }

    componentWillMount() {

        axios.get(apinewtopic)
        .then(res => {
            console.log("New topic",res)
            const data = res.data

            this.setState({
                newtopic : res.data,
                articles: res.articles
            })

            console.log("data New topic : ",data)

        })
        .catch( err => 
                console.log(err)
            )
    }

    handdleonClick() {

        const idtopic = this.state.newtopic
        console.log("topic detail", idtopic)


    }


    render() {

        const ultopic = this.state.newtopic
        console.log("ultopic" ,ultopic)


        return (

            <div className="news">


                <h2>ประชาสัมพันธ์</h2>
                <ul> 
                        {

                            ultopic.map(topic => <li key={topic.id}> <input type="button" value={topic.id} onClick={this.handdleonClick}  /> {topic.topic} </li>)

                        }   


                </ul>




            </div>
        )
    }
}
