import React, { Component } from 'react'
import Banner from './components/Home/Header/Banner/Banner'
import InputLogin from './components/FormLogin/Login'
// import Contact from './components/Home/Contact/Contact'
import NameHotel from './components/Home/Header/NameHotel/NameHotel'
import News from './components/Home/News/News'
import ImgSlideBar from './components/Home/Header/IMGSlidebar/ImgSlideBar'
// import GoogleMap from './components/Home/Assets/API/GoogleMap'
// import Reg from './components/Register/Reg'
import Home from './components/Home/Home/Home'
import { BrowserRouter as Router } from 'react-router-dom'

import './App.css'
import axios from 'axios'

const apiuser = 'http://localhost:9999/user'

export default class App extends Component {

  constructor(props){
    super(props)


    this.state = {

      token : "",

    }
  }

  // componentdidmount
  componentDidMount = () => {

    const config = {
      headers: {
        Authorization : localStorage.getItem('token')

      }
    }
    this.setState({
      token : localStorage.getItem('token')
    })

    console.log("token Check",localStorage.getItem('token'))

    // api axios นำ email มาแสดง
    axios.get(apiuser,config)

      .then(
      res => {

        // เซต Email มาแสดง
        this.setState({
          email: res.data[0].email
        })

        // console.log("res",res)
        console.log("resdata : " , res.data[0].email)


        for (var i = 0; i < res.data.length; i++) {
          console.log(res.data[i]);
        } 
      },
      err => {
        console.log(err)
      }
    )
  }


  render() { 

    const token = localStorage.getItem('token')

    console.log(token)

    if (token){
      return (
        <Router>
        <div className="header">
            <div className="container">

              <div className="input">
                <InputLogin />

              </div>

              <div>

              <Home email={this.state.email} />
              <p> Token Test: {this.state.token} </p>
              </div>

                <Banner /> 

              <div className="slidebar-app">
                <ImgSlideBar />
              </div>

              <div className= "namehotel" >
                <NameHotel />
              </div>

              <div className="news">
                <News />
              </div>

              {/* <div className="mapgoogle">
                <GoogleMap />
              </div> */}

          </div>

          {/* <div className="footer">
                <Contact />
              </div>  */}

        </div>
        </Router>
      )
    }

  return (
      <Router>
      <div className="header">
          <div className="container">

            <div className="input">
              <InputLogin />

            </div>

            <div>

            <Home email={this.state.email} />
            <p> Token Test: {this.state.token} </p>
            </div>

              <Banner /> 

            <div className="slidebar-app">
              <ImgSlideBar />
            </div>

            <div className= "namehotel" >
              <NameHotel />
            </div>

            <div className="news">
              <News />
            </div>

            {/* <div className="mapgoogle">
              <GoogleMap />
            </div> */}

        </div>

        {/* <div className="footer">
              <Contact />
            </div>  */}

      </div>
      </Router>
    )
  }
}
