import React, { Component } from 'react'
import axios from 'axios'


import './News.css'

 const apinewtopic ="http://localhost:9999/new"

export default class News extends Component {

    constructor(props){
        super(props)
        this.state ={
            newtopic: [],
            id : "",
            topic : "",
            detail : ""
        }
    }

    componentWillMount() {

        axios.get(apinewtopic)
        .then(res => {
            console.log("New topic",res)
            const data = res.data

            const ul = data.map( d => ({
                "id" : d.id,
                "topic" : d.topic,
                "detail" : d.detail
            }))

            const listItems = data.map((number) =>
                    <li key={number.toString()}>
                    {number}
                    </li>
                )

            // const listli = data.map((data) => 
            // <li key={data.id} > {data} </li>
            // )
            this.setState({
                newtopic : listItems
            })

            console.log(data)

        })
        .catch( err => 
                console.log(err)
            )
    }

    render() {

        console.log("render news topic ", this.state.newtopic )


        return (

            <div className="news">

            <from>
                <h2>ประชาสัมพันธ์</h2>
                <ul> {listItems}  </ul>

            </from>

            </div>
        )
    }
}
