/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react'
import axios from 'axios'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import Reg from '../Register/Reg'
import Booking from '../Member/Booking/Booking'


import './InputLogin.css'

 const api = 'http://localhost:9999/login'

export default class InputLogin extends Component {


    constructor(props){
        super(props);

        this.handleLogin = this.handleLogin.bind(this);
        this.handleLogoutClick = this.handleLogoutClick.bind(this);


        this.state = {

            isLoggedIn : false
        }
    }

      handleLogin = e => {

        this.setState ({
            isLoggedIn: true
        })

          e.preventDefault();

        const con = {
            method: 'post',
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded',
            }
        }

        const data  = {
              email : this.email,
              password: this.password,
              usepermis_id : this.usepermis_id,
          };

        axios.post(api,data,con  )
        .then(function(res) {

            localStorage.setItem('token',res.data.token)

            console.log(res.data.type)
            console.log(res.data.firstname)

          })

        .catch(err => {
            console.log(err)
        })
      };

      handleLogoutClick() {
        this.setState ({
            isLoggedIn: false
        })
        localStorage.clear()
    }

    render() {





        return (

            <Router>
            <div className="row">

                    <div className="col">

                    <form onSubmit={this.handleLogin}  >

                        <div className="form-group">
                            <label >Email</label>
                            <input
                            type="email"
                            name="email"
                            onChange={e => this.email = e.target.value} 
                            className="form-control" 
                            placeholder="Email"
                            required />

                            <label>Password</label>
                            <input 
                            type="password"
                            name="password"
                            onChange={e => this.password = e.target.value} 
                            className="form-control" 
                            placeholder="Password"
                            required />
                           <button onClick={this.handleLogin}>Login</button>
                        </div>      

                    </form>
                    <a href="/Register/Reg">สมัครสมาชิก</a>
                        <Switch>
                            <Route path='/Register/Reg' component={Reg} />
                        </Switch>    
                    </div>
            </div>
            </Router>
        )
        }
    }
