import React, { Component } from 'react'
import axios from 'axios'

import './EditMem.css'

const apiuser = 'http://localhost:9999/user'

export default class EditMem extends Component {

    constructor(props){
        super(props)
        this.state = {

            email: "",
            firstname:"",
            lastname:"",
            birthday:"",
            tel:""
        }

         this.haldleChangeSubmit = this.haldleChangeSubmit.bind(this);

    }

    componentWillMount(){

        const config = {
            headers: {
              Authorization : localStorage.getItem('token')

            }
          }

          console.log("edit",localStorage.getItem('token'))

          // api axios นำ email มาแสดง
          axios.get(apiuser,config)

            .then(
            res => {

              // เซต Email มาแสดง
              this.setState({
                id: res.data[0].id,
                email: res.data[0].email,
                firstname: res.data[0].firstname,
                lastname: res.data[0].lastname,
                birthday: res.data[0].birthday,
                tel: res.data[0].tel,
              })
              console.log("componentWillMount id  : ", this.state.id)

            },
            err => {
              console.log(err)
            }
          )
    }

    haldleChangeSubmit(e){

      e.preventDefaule()
      const configedit = {
        headers: {
          Authorization : localStorage.getItem('token')

        }
      }

      const dataEdit = {
        firstname: this.firstname,
        lastname: this.lastname,
        tel: this.tel,
        password : this.password
      }

      console.log("haldleChangeSubmit",localStorage.getItem('token'))
      console.log("ID edit : ",this.state.id )

      axios.put(`http://localhost:9999/edituser/${this.state.id}`,configedit,dataEdit)

        .then( res => {
          console.log(res)

          alert("ยืนยันการเปลี่ยน")
        },
        err => {
          console.log(err)
        }
        )
    }

    render() {
        return (
            <div className="formEdit">

                <div className="header-Reg">
                    <img src="https://cdn2.f-cdn.com/contestentries/365819/10129715/56e657a9c8e95_thumb900.jpg" alt="Logo" />
                </div>

                <form onSubmit={this.haldleChangeSubmit} >
                    <h2>แก้ไขข้อมูล</h2> <br />
                    <div className="table-Eidt">

                        <table>

                            <tbody>

                                <tr>
                                    <td> <label>ชื่อ: </label></td>
                                    <td> <input name="firstname" 
                                    ref="name"
                                    type="text" 
                                    className="form-edit"  
                                    placeholder="ชื่อ" 
                                    value={this.state.firstname} 
                                    onChange={e => this.setState({ firstname: e.target.value })} /> </td>
                                </tr>

                                <tr>
                                    <td> <label>นามสกุล: </label></td>
                                    <td> <input name="lastname"
                                    type="text" 
                                    className="form-edit"  
                                    placeholder="นามสกุล" 
                                    value={this.state.lastname} 
                                    onChange={e => this.setState({ lastname: e.target.value })} /></td>
                                </tr>

                                <tr>
                                    <td> <label>เบอร์โทรศัพท์: </label></td>
                                    <td><input name="tel"
                                    type="text" 
                                    className="form-edit" 
                                    placeholder="เบอร์โทรศัพท์" 
                                    value={this.state.tel} 
                                    onChange={e => this.setState({ tel: e.target.value })} /></td>
                                </tr>

                                <tr>
                                    <td> <label>รหัสผ่าน: </label></td>
                                    <td><input name="password"
                                    type="password" 
                                    className="form-edit" 
                                    placeholder="password" 
                                    required /></td>
                                </tr>

                            </tbody>

                        </table>

                    </div>

                    <div className="btn-edit">
                        <button type="submit" className="btn">ยืนยัน</button>
                    </div>


                </form>
            </div>
        )
    }
}
