import React, { Component } from 'react'
import axios from 'axios'

import './EditMem.css'

const edituser = ""
const apiuser = 'http://localhost:9999/user'

export default class EditMem extends Component {

    constructor(props){
        super(props)
        this.state = {

            email: "",
            firstname:"",
            lastname:"",
            birthday:"",
            tel:""
        }

        this.handleChange = this.handleChange.bind(this);

    }


    componentWillMount(){

        const config = {
            headers: {
              Authorization : localStorage.getItem('token')

            }
          }

          console.log("edit",localStorage.getItem('token'))

          this.setState({
            token : localStorage.getItem('token')
          })

          // api axios นำ email มาแสดง
          axios.get(apiuser,config)

            .then(
            res => {

              // เซต Email มาแสดง
              this.setState({
                email: res.data[0].email,
                firstname: res.data[0].firstname,
                lastname: res.data[0].lastname,
                birthday: res.data[0].birthday,
                tel: res.data[0].tel,
              })
              console.log("name  : ", this.state.firstname)

            },
            err => {
              console.log(err)
            }
          )
    }

    componentDidMount(){
        console.log("EditUsertoken",localStorage.getItem('token'))

        const config = {
            headers: {
              Authorization : localStorage.getItem('token')

            }
          }
          this.setState({
            token : localStorage.getItem('token')
          })

          // api axios แก้ไขข้มูล
          axios.get(edituser,config)

            .then(
            res => {

                console.log("editmem",res)

            },
            err => {

              console.log(err)

            }
          )
    }

    // handleChange(e) {
    //     this.state.updateField(e.target.name, e.target.value)
    // }

    handleChange(event) {    


        this.setState({
            firstname: event.target.value}
            );  
    }


    render() {
        return (
            <div className="formEdit">

                <div className="header-Reg">
                    <img src="https://cdn2.f-cdn.com/contestentries/365819/10129715/56e657a9c8e95_thumb900.jpg" alt="Logo" />
                </div>

                <form><br /> 
                    <h2>แก้ไขข้อมูล</h2> <br />
                    <div className="table-Eidt">

                        <table>

                            <tbody>

                                <tr>
                                    <td> <label>ชื่อ: </label></td>
                                    <td> <input name="firstname" type="text" className="form-edit"  placeholder="ชื่อ" value={this.state.firstname} onChange={this.handleChange} /> </td>
                                </tr>

                                <tr>
                                    <td> <label>นามสกุล: </label></td>
                                    <td> <input type="text" className="form-edit"  placeholder="นามสกุล" value={this.state.lastname} /></td>
                                </tr>

                                <tr>
                                    <td> <label>วันเกิด: </label></td>
                                    <td> <input type="Date" className="form-edit"placeholder="วันเกิด" value={this.state.birthday} /></td>
                                </tr>

                                <tr>
                                    <td> <label>เบอร์โทรศัพท์: </label></td>
                                    <td><input type="text" className="form-edit" placeholder="เบอร์โทรศัพท์" value={this.state.tel} /></td>
                                </tr>

                                <tr>
                                    <td> <label>รหัสผ่าน: </label></td>
                                    <td><input type="password" className="form-edit" placeholder="password" required /></td>
                                </tr>

                            </tbody>

                        </table>

                    </div>

                    <div className="btn-edit">
                        <button type="submit" className="btn">ยืนยัน</button>
                    </div>


                </form>
            </div>
        )
    }
}
