/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react'
import axios from 'axios'
import { BrowserRouter as Router, Route } from 'react-router-dom'
import Reg from '../Register/Reg'


import './InputLogin.css'

 const api = 'http://localhost:9999/login'

export default class InputLogin extends Component {


    constructor(props){
        super(props);

        this.handleLogin = this.handleLogin.bind(this);
    }

      handleLogin = e => {

          e.preventDefault();

        const con = {
            method: 'post',
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded',
            }
        }

        const data  = {
              email : this.email,
              password: this.password,
              usepermis_id : this.usepermis_id,
          };

        axios.post(api,data,con  )
        .then(function(res) {

            localStorage.setItem('token',res.data.token)

            console.log(res.data.type)
            console.log(res.data.firstname)

          })

        .catch(err => {
            console.log(err)
        })
      };

    render() {

        return (


            <div className="row">

                    <div className="col">

                    <form onSubmit={this.handleLogin}  >

                        <div className="form-group">
                            <label >Email</label>
                            <input
                            type="email"
                            name="email"
                            onChange={e => this.email = e.target.value} 
                            className="form-control" 
                            placeholder="Email"
                            required />

                            <label>Password</label>
                            <input 
                            type="password"
                            name="password"
                            onChange={e => this.password = e.target.value} 
                            className="form-control" 
                            placeholder="Password"
                            required />
                           <button>Login</button>
                        </div>      

                    </form>

                    <Router>
                    <a href="/Register/Reg">สมัครสมาชิก</a>
                            <Route path="/" exact component={() => <Reg />}/>
                            <Route exact path='/Register/Reg' component={Reg} />
                    </Router>

                    </div>

            </div>

        )
        }
    }
