/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react'
import axios from 'axios'
import { BrowserRouter as Router,Link, Route, Switch } from 'react-router-dom'
import Reg from '../Register/Reg'
import './InputLogin.css'

 const api = 'http://localhost:9999/login'
// const api2 = 'https://ufa365xr.com/apiweb/api.php'

export default class InputLogin extends Component {


    constructor(props){
        super(props);

        this.handleLoginClick = this.handleLogin.bind(this);

        this.state = {
            isLoggedIn : false
        }
    }

      handleLogin = e => {

        this.setState ({
            isLoggedIn: true
        })

          e.preventDefault();

        const con = {
            method: 'post',
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded',
            }}

        const data  = {
              email : this.email,
              password: this.password,
              usepermis_id : this.usepermis_id,
          };

        axios.post(api,data,con  )
        .then(function(res) {
            console.log(res);


          })

        .catch(err => {
            console.log(err)
        })
      };


    render() {

        const isLoggedIn = this.state.isLoggedIn;
        let button;

        if (isLoggedIn) {
            button = <LogoutButton onClick={this.handleLogoutClick}/>
        } else {
            button = <LoginButton onClick={this.handleLoginClick}/>

        }

        return (

            <Router>
            <div className="row">

                    <div className="col">

                    <form onSubmit={this.handleLogin} >

                        <div className="form-group">
                            <label >Email</label>
                            <input
                            type="email"
                            name="email"
                            onChange={e => this.email = e.target.value} 
                            className="form-control" 
                            placeholder="Email"
                            required />

                            <label>Password</label>
                            <input 
                            type="password"
                            name="password"
                            onChange={e => this.password = e.target.value} 
                            className="form-control" 
                            placeholder="Password"
                            required />

                            <button type="submit">Login</button>

                            <Link to={'/Register/Reg'} className="nav-link"> สมัครสมาชิก </Link>
                        <hr />
                        <Switch>
                            <Route path='/Register/Reg' component={Reg} />
                        </Switch>

                        </div>


                    </form>
                    </div>

            </div>
            </Router>

        )
    }
}
function Login(props) {
    return (
        <div>
             <p>Hello</p>
        </div>
    )

}

function GustGreeting(props) {
    return <p>Please sige up.</p>
}

function Greeting(props) {
    const isLoggedIn = props.isLoggedIn;

    if(isLoggedIn) {
        return <Login />
    } else {
        return <GustGreeting />
    }
}

function  LoginButton(props) {
    return (
        <button onClick={props.onClick}>
            Login
        </button>
    )
}

function  LogoutButton(props) {
    return (
        <button onClick={props.onClick}>
            Logout
        </button>
    )
}
