/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'
import moduleName from './Register/'
import './InputLogin.css'

export default class InputLogin extends Component {
    constructor(props) {
        super(props)
        this.state ={
          username: "",
          password: ""
        };
      }
    render() {
        return (
            <Router>
            <div className="row">

                    <div className="col">

                    <form action="" onSubmit={() => alert(JSON.stringify(this.state.username))} >

                        {/* Usename */}
                        <div className="form-group">
                            <label >Username</label>
                            <input 
                            onChange={e => {this.setState({username: e.target.value})}}
                            value={this.state.username} 
                            type="text" 
                            className="form-control" 
                            placeholder="Username" />

                            <label>Password</label>
                            <input 
                            onChange={e => {this.setState({password: e.target.value})}}
                            value={this.state.password} 
                            type="password" 
                            className="form-control" 
                            placeholder="Password" />

                            <button type="submit">เข้าสู่ระบบ</button>
                            <Link to={'Register/Reg'} className="nav-link" >Reg</Link>

                            <Switch>

                            <Route  path='Register/Reg' />

                            </Switch>

                        </div>


                    </form>

                    </div>

            </div>
            </Router>
        )
    }
}
