import React, { Component } from 'react'
import Home from './components/Home'
import Singin from './components/Register/Reg'
import { BrowserRouter as Router,NavLink, Route } from 'react-router-dom'

import './App.css'

export default class App extends Component {


  constructor(props) {
    super(props);

    this.handleShow = this.handleShow.bind(this);
    this.handleHide = this.handleHide.bind(this);


    this.state = {
        isLoggedIn: false
    }
  } 

  handleShow(){
    this.setState ({
      isLoggedIn: true
    })
  }

  handleHide() {
    this.setState ({
        isLoggedIn: false
    })
}


  componentDidMount(){
    console.log("123456789",localStorage.getItem('token'))
  }

  render() { 


    const isLoggedIn = this.state.isLoggedIn;
    let button;

    if (isLoggedIn) {
      button = <LogoutButton onClick={this.handleHide}/>
  } else {
      button = <LoginButton onClick={this.handleShow}/>
  }

  return (
      <Router>

      <div className="header"> 
        <div className="navLink">
          <NavLink to='/' ><button className="btn btn-dark">Home</button></NavLink>
          <NavLink to='/Singin' ><button className="btn btn-dark">Sing in</button></NavLink>
          {button}

            <Route exact path='/' component={Home} />
            <Route path='/Singin' component={Singin} />
        </div>

      </div>
      </Router>
    )
  }
}

function  LoginButton(props) {
  return (
      <button onClick={props.onClick}>
          Login
      </button>
  )
}

function  LogoutButton(props) {
  return (
      <button onClick={props.onClick}>
          Logout
      </button>
  )
}
