import React, { Component } from 'react'

import axios from 'axios'


import './News.css'

 const apinewtopic ="http://localhost:9999/new"

export default class News extends Component {

    constructor(props){
        super(props)

        this.handdleonClick = this.handdleonClick.bind(this)


        this.state ={
            newtopic: [],
            id : "",
            topic : "",
            detail : ""
        }
    }

    componentWillMount() {

        axios.get(apinewtopic)
        .then(res => {
            console.log("New topic",res)
            const data = res.data

            this.setState({
                newtopic : res.data
            })

            console.log("data New topic : ",data)

        })
        .catch( err => 
                console.log(err)
            )
    }

    handdleonClick() {

        const idtopic = this.state.newtopic
        console.log("topic detail", idtopic)

        console.log(idtopic.map( (index => {
                    <div key={index.id}>
                        <h3>{index.detail
                        }</h3>
                    </div>
                    }))
                )

        // idtopic.map( (idtopic , index => {
        //     <h2 key={index} > {index} {idtopic} </h2>
        // }) )

    //     const content = props.posts.map((post) =>
    //     <div key={post.id}>
    //       <h3>{post.title}</h3>
    //       <p>{post.content}</p>
    //     </div>
    //   );


    }

    render() {

        const ultopic = this.state.newtopic
        console.log("ultopic" ,ultopic)


        return (

            <div className="news">


                <h2>ประชาสัมพันธ์</h2>
                <ul> 
                        {

                            ultopic.map(topic => <li> <input type="button" value="คลิก" onClick={this.handdleonClick} /> {topic.topic} </li>)

                        }   

                </ul>


            </div>
        )
    }
}
