import React, { Component } from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'

import './InputLogin.css'

export default class InputLogin extends Component {
    constructor(props) {
        super(props)
        this.state ={
          username: "",
          password: ""
        };
      }
    render() {
        return (
            <Router>
            <div className="row">

                    <div className="col-xs-10 col-sm-10 col-md-10 col-lg-10">

                    <form action="" onSubmit={() => alert(JSON.stringify(this.state))} >

                        {/* Usename */}
                        <div className="form-group">
                            <label >Username</label>
                            <input 
                            onChange={e => {this.setState({username: e.target.value})}}
                            value={this.state.username} 
                            type="text" 
                            className="form-control" 
                            placeholder="Username" />

                            <label>Password</label>
                            <input 
                            onChange={e => {this.setState({password: e.target.value})}}
                            value={this.state.password} 
                            type="password" 
                            className="form-control" 
                            placeholder="Password" />

                        </div>
                        <div className="btn">
                            <button type="submit" className="btn-login">เข้าสู่ระบบ</button> <br />
                            <Link to={'/'} className="nav-link"> สมัสมาชิก </Link>
                        </div>

                    </form>

                    </div>

            </div>
            </Router>
        )
    }
}
