import React, { Component } from 'react'
import axios from 'axios'



import './Booking.css';

const apiuser = 'http://localhost:9999/user'
const apiBooking = "http://localhost:9999/booking"
const apislectroom = "http://localhost:9999/selectroom"



export default class Booking extends Component {

    constructor(props){
        super(props)

        this.onSubmitBooking = this.onSubmitBooking.bind(this)
        this.handleRoom = this.handleRoom.bind(this);


        this.state= {
            id: "",
            create_from : "",
            create_to : "",
            room_id: "",
            name_room: "",
            price: 0

        }
    }

    componentWillMount(){

        const config = {
            headers: {
              Authorization : localStorage.getItem('token')

            }
          } 
          console.log(localStorage.getItem('token'))     
          // api axios นำ email มาแสดง
          axios.get(apiuser,config)

            .then(
            res => {

                console.log(res)
              // เซต Email มาแสดง
              this.setState({
                id: res.data[0].id
              })      
            }
            )
            .catch(
            err => {
                console.log(err)
            }
        )

    }


    handleRoom(){

        const configroom = {
            headers: {
              'Content-Type': 'application/json'

            }
          }

        axios.get(apislectroom ,configroom)
        .then(
            res => {

                for (var i = 0; i < res.data.length; i++) {
                    this.setState({

                    })
                    console.log("len")
                    console.log("=====",res.data[i]);
                  } 

                console.log("email",this.state.email)   

              }

        )
        .catch(
            err => {
                console.log(err)
            }
        )


    }

    onSubmitBooking(e){

        e.preventDefault();

        const configBooking = {
            headers: {
              'Content-Type': 'application/json'

            }
          }

        const data = {
            user_id : this.state.id,
            room_id : this.state.room_id,
            create_from : this.create_from,
            create_to : this.create_to,

        }

        console.log(data)
        console.log(JSON.stringify(data))

        axios.post(apiBooking,data,configBooking)
        .then( res => {
            console.log(res.data)
        }
        )
        .catch(
            err => {
                console.log(err)
            }
        )
    }

    render() {

        console.log("room_id ", this.state.room_id )
        console.log("render Booking id: ", this.state.id)

        const id_room = this.state.room_id

        console.log(id_room)



        console.log("price", this.state.price)

        return (            
            <div className="booking"> 
                <div className="header-Booking">
                <img src="https://cdn2.f-cdn.com/contestentries/365819/10129715/56e657a9c8e95_thumb900.jpg" alt="Logo" />
                </div>
            <form onSubmit={this.onSubmitBooking}>
            <table border="0">
                                    <tbody>
                                        <tr>
                                            <td>
                                                <label>วันที่จอง</label>
                                            </td>
                                            <td>
                                            <input name="user_id" 
                                                type="hidden" 
                                                className="form-edit"  
                                                value={this.state.id}
                                                onChange={e => this.setState({ user_id: e.target.value })}  /> 

                                            <input name="create_from" 
                                                type="date" 
                                                className="form-edit"  
                                                onChange={e => this.create_from =  e.target.value} /> 
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>
                                            <label>ถึงวันที่</label>
                                            </td>
                                            <td>
                                            <input name="create_to" 
                                                type="date" 
                                                className="form-edit"  
                                                onChange={e => this.create_to = e.target.value} /> 
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>
                                                <label>เลือกประเภทห้อง</label>
                                            </td>
                                            <td>
                                            <input name="room_id" 

                                                className="form-edit"  
                                                value={this.state.room_id}
                                                onChange={e => this.setState({ room_id: e.target.value })}
                                                />

                                                {/* <select onSubmit={this.handleRoom} onChange={this.handleChange}>
                                                    <option>เลือกประเภทห้อง</option>
                                                    <option name="room_id" value={n1} >ห้อง A</option>
                                                    <option name="room_id" value="2" >ห้อง B</option>
                                                    <option name="room_id" value="3" >ห้อง C</option>
                                                </select> */}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>
                                                <label>ราคา</label>
                                            </td>
                                            <td>
                                                <input type="text" 
                                                defaultValue={this.state.price} 
                                                />
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>
                                                <label>รายละเอียด</label>
                                            </td>
                                            <td>
                                                <textarea defaultValue="Detali" />
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>
                                                <label>รวมเป็นเงิน</label>
                                            </td>
                                            <td>
                                                <input type="text" defaultValue="..." />
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>
                                                <label>ช่องทางชำระเงิน</label>
                                            </td>
                                            <td>
                                                <select>
                                                    <option>ชำระผ่าน 7-11</option>
                                                    <option>ชำระผ่าน paypal</option>
                                                </select>
                                            </td>
                                        </tr>

                                    </tbody>
                                </table>

                    <div className="btn-booking">
                            {/* <button type="submit" className="btn">ตกลง</button> */}
                            <input type="button" onClick={this.onSubmitBooking} value="Submit" />
                            {/* <input type="button" value="Submit" /> */}
                    </div>
            </form>
            </div>
        )
    }
}
