import React, { Component } from 'react'
import Banner from './components/Home/Header/Banner/Banner'
import InputLogin from './components/FormLogin/Login'
// import Contact from './components/Home/Contact/Contact'
import NameHotel from './components/Home/Header/NameHotel/NameHotel'
import News from './components/Home/News/News'
import ImgSlideBar from './components/Home/Header/IMGSlidebar/ImgSlideBar'
// import GoogleMap from './components/Home/Assets/API/GoogleMap'
//import Reg from './components/Register/Reg'
import { BrowserRouter as Router } from 'react-router-dom'

import './App.css'
import axios from 'axios'

const apiuser = 'http://localhost:9999/user'

export default class App extends Component {

  constructor(props){
    super(props)
    this.state = {
      user : []
    }
  }

  componentDidMount = () => {

    const config = {
      headers: {
        Authorization : localStorage.getItem('token')

      }

    }
    console.log("token",localStorage.getItem("token"))

    console.log("==componentDidMount==")

    axios.get(apiuser,config)

      .then(
      res => {
        console.log(res)
        this.setState({user: res.data})
        console.log("data : ")
      },
      err => {
        console.log(err)
      }
    )

  }


  render() {
    const { user } = this.state

    return (
      <Router>
      <div className="header">
          <div className="container">

            <div className="input">
              <InputLogin />

              <p>
                Welcome
              </p>
              {

                user.map(user => <div key={user.id}> {user.fristname} </div>) 
              }

            </div>

              <Banner /> 

            <div className="slidebar-app">
              <ImgSlideBar />
            </div>

            <div className= "namehotel" >
              <NameHotel />
            </div>

            <div className="news">
              <News />
            </div>

            {/* <div className="mapgoogle">
              <GoogleMap />
            </div> */}

        </div>

        {/* <div className="footer">
              <Contact />
            </div>  */}

      </div>
      </Router>
    )
  }
}
